		// TODO Auto-generated method stub
		
		
		
		//8Primitive datatypes
				//To store whole numbers --> byte, short, int, long
				byte variableOne=10;
				short variable2=100;
				int variable3=1000;
				long varaible4=10000000;
				
				//To store decimal numbers ---.float, double
				float varaible$Five=12.99f;
				double anyName=123.89;
				
				
				//To store single character 
				char SingleCHar='a';
